    ["Wohnort"] = { -- Hidden residence prompts (First %s: First name / Second %s: Age / Third %s: First letter of Residence, Fourth %s: Last letter of Residence)
        [1] = "Lerne %s, das Bankenwunderkind, kennen. Mit nur %s Jahren ist er einer der jüngsten und brillantesten Datenanalysten im Bundesstaat %s---%s. Als Visionär mit unerschütterlicher Integrität strebt Alex danach, den Bankensektor zu revolutionieren und einen bleibenden Eindruck auf die finanzielle Zukunft der Kunden zu hinterlassen.",
