    ["đầu tiên"] = { -- Hidden first name prompts (Name: first-last letter / state / age)
        [1] = "%s---%s từ %s, một phù thủy tài chính %s tuổi, hướng dẫn khách hàng đến thành công với khả năng thích ứng và chuyên môn của mình. Kỹ năng giải quyết vấn đề đáng chú ý của họ làm cho anh ấy trở thành người đáng tin cậy trong các vấn đề tài chính phức tạp.",
        [2] = "%s---%s từ %s, %s tuổi, trao quyền cho khách hàng với các chiến lược sáng tạo, làm cho anh ấy trở thành một ngôi sao đang lên trong thế giới ngân hàng. Cam kết của họ trong việc cập nhật các xu hướng tài chính mới nhất đảm bảo khách hàng nhận được các giải pháp tiên tiến.",
        [3] = "%s---%s ở %s, một chuyên gia ngân hàng %s tuổi, để lại ấn tượng lâu dài với hướng dẫn tài chính cá nhân. Nổi tiếng với cách tiếp cận đồng cảm của mình, cô ấy thiết lập mối quan hệ bền vững và lâu dài với khách hàng.",
