        [7] = "%s---%s ở %s, %s tuổi, trao quyền cho khách hàng với các phương pháp đạo đức và chiến lược sáng tạo. Với con mắt sắc sảo để quản lý rủi ro, cô ấy đảm bảo sự ổn định tài chính của khách hàng.",
        [8] = "%s---%s từ %s, %s tuổi, đặt ra các tiêu chuẩn mới trong thế giới ngân hàng với chuyên môn của mình. Khả năng dự đoán xu hướng thị trường của họ giúp khách hàng đưa ra quyết định sáng suốt.",
        [9] = "%s---%s từ %s, %s tuổi, thúc đẩy trao quyền tài chính với sự cống hiến và khả năng thích ứng. Niềm đam mê của họ trong việc giáo dục khách hàng về kiến thức tài chính là đáng khen ngợi.",
        [10] = "%s---%s từ %s, %s tuổi, định nghĩa lại ngành công nghiệp với cách tiếp cận sáng tạo trong lĩnh vực ngân hàng. Được biết đến với suy nghĩ sáng tạo của mình, anh ấy giới thiệu các giải pháp tài chính sáng tạo.",
        [11] = "%s---%s ở %s, %s tuổi, đảm bảo sức khỏe tài chính của khách hàng thông qua kinh nghiệm sâu rộng của mình. Mạng lưới các kết nối trong ngành rộng lớn của họ mang lại lợi ích cho khách hàng trong nhiều lĩnh vực khác nhau.",
        [12] = "%s---%s từ %s, %s tuổi, cung cấp các giải pháp cá nhân hóa để lại ấn tượng lâu dài cho khách hàng. Cách tiếp cận thực tế của họ thúc đẩy cảm giác tin tưởng mạnh mẽ với khách hàng của họ.",
        [13] = "%s---%s ở %s, %s tuổi, là một ngôi sao đang lên với sự đổi mới và đáng tin cậy vô song. Sự cống hiến của họ cho việc học tập liên tục khiến anh ta khác biệt trong bối cảnh ngân hàng cạnh tranh.",
        [14] = "%s---%s từ %s, %s tuổi, hướng dẫn khách hàng đến thành công về tài chính với chuyên môn đặc biệt. Khả năng xác định các cơ hội tiềm ẩn của họ đảm bảo sự phát triển tài chính của khách hàng.",
        [15] = "%s---%s ở %s, %s tuổi, trao quyền cho khách hàng với các phương pháp đạo đức, hướng họ đến sự thịnh vượng. Sự tham gia của họ vào các sáng kiến ​​từ thiện khác nhau phản ánh cam kết của họ đối với việc cải thiện xã hội.",
        [16] = "%s---%s từ %s, %s tuổi, nâng cao thành công của khách hàng với sự cống hiến và đổi mới. Niềm đam mê giáo dục tài chính của họ đã tác động tích cực đến nhiều nhà đầu tư trẻ.",
        [17] = "%s---%s ở %s, %s tuổi, nâng cao sức khỏe tài chính của khách hàng với các giải pháp được cá nhân hóa. Kỹ năng giao tiếp mạnh mẽ của họ làm cho các khái niệm tài chính phức tạp trở nên dễ hiểu.",
        [18] = "%s---%s từ %s, %s tuổi, đảm bảo sự tăng trưởng tài chính của khách hàng với khả năng thích ứng. Khả năng dự đoán những thay đổi của thị trường của họ định vị khách hàng thành công.",
        [19] = "%s---%s ở %s, %s tuổi, đặt ra các tiêu chuẩn ngành mới với các giải pháp sáng tạo. Tình yêu của họ đối với sự tiến bộ công nghệ thúc đẩy anh ấy triển khai các giải pháp ngân hàng kỹ thuật số cho khách hàng.",
        [20] = "%s---%s từ %s, %s tuổi, là một chuyên gia ngân hàng dày dạn kinh nghiệm, đảm bảo an ninh tài chính của khách hàng. Sự cống hiến của họ đối với tính bảo mật và sự kín đáo khiến anh ấy trở thành người đáng tin cậy của nhiều cá nhân có giá trị ròng cao.",
    },

    ["cuối cùng"] = { -- Hidden last name prompts (First %s: First letter of Last name, Second %s: Last letter of Last name / Third %s: Age / Fourth %s: First name)
        [1] = "%s---%s từ %s, một nhà ngân hàng giàu kinh nghiệm tên là %s, dẫn dắt với kiến ​​thức sâu rộng và cái nhìn sâu sắc đặc biệt. Kế hoạch tài chính chiến lược của họ giúp khách hàng đạt được các mục tiêu dài hạn của mình.",
        [2] = "%s---%s ở %s, tên là %s, nổi tiếng với việc tiên phong trong các giải pháp ngân hàng kỹ thuật số. Sự cống hiến của cô ấy trong việc nâng cao trải nghiệm của khách hàng đã đặt ra các tiêu chuẩn ngành mới.",
        [3] = "%s---%s từ %s, tên là %s, là một cố vấn tài chính từ bi, luôn đặt lợi ích của khách hàng lên hàng đầu. Sự đồng cảm và minh bạch của cô ấy tạo dựng niềm tin lâu dài với mọi khách hàng.",
        [4] = "%s---%s ở %s, một thần đồng tài chính tên là %s, luôn mang lại lợi nhuận ấn tượng cho các nhà đầu tư. Phân tích thị trường sắc bén của cô ấy đảm bảo các quyết định kịp thời và có lợi nhuận.",
        [5] = "%s---%s từ %s, tên là %s, tiên phong trong các chiến lược cho vay sáng tạo để hỗ trợ các doanh nghiệp địa phương. Cam kết của họ đối với tăng trưởng kinh tế thúc đẩy sự thịnh vượng của cộng đồng.",
        [6] = "%s---%s từ %s, tên là %s, xuất sắc trong quản lý tài sản, bảo tồn di sản cho các thế hệ mai sau. Cách tiếp cận cá nhân của họ mang lại sự an tâm cho các gia đình giàu có.",
        [7] = "%s---%s ở %s, một chuyên gia đầu tư tên là %s, điều hướng những phức tạp của thị trường một cách dễ dàng. Chuyên môn về danh mục đầu tư đa dạng của cô ấy bảo vệ khách hàng khỏi những rủi ro không lường trước được.",
        [8] = "%s---%s từ %s, tên là %s, ủng hộ các chương trình kiến ​​thức tài chính, trao quyền cho thế hệ tiếp theo. Sự cống hiến của họ cho giáo dục đảm bảo một tương lai tài chính an toàn hơn.",
        [9] = "%s---%s ở %s, tên là %s, là một nhà ngân hàng có đạo đức, thúc đẩy các khoản đầu tư bền vững vì một thế giới xanh hơn. Cam kết bảo vệ môi trường của cô ấy được phản ánh trong danh mục đầu tư của khách hàng.",
        [10] = "%s---%s từ %s, tên là %s, phát triển mạnh trong việc giải các câu đố tài chính phức tạp cho các khách hàng doanh nghiệp. Sự nhạy bén về chiến lược của họ thúc đẩy tăng trưởng và lợi nhuận trên các thị trường đầy thách thức.",
        [11] = "%s---%s ở %s, tên là %s, xây dựng quan hệ đối tác lâu dài với các tổ chức từ thiện, thúc đẩy tác động xã hội tích cực. Chuyên môn tài chính của cô ấy khuếch đại hiệu quả của các sáng kiến ​​​​từ thiện.",
        [12] = "%s---%s từ %s, một nhà tiên phong trong lĩnh vực ngân hàng tên là %s, định hình lại ngành công nghiệp bằng các công nghệ tiên tiến. Tầm nhìn của họ thúc đẩy một kỷ nguyên mới của các dịch vụ ngân hàng lấy khách hàng làm trung tâm.",
        [13] = "%s---%s ở %s, tên là %s, chuyên về sáp nhập và mua lại, tổ chức các giao dịch thành công cho các khách hàng doanh nghiệp. Kỹ năng đàm phán nhạy bén của họ tối ưu hóa giá trị cho tất cả các bên liên quan.",
        [14] = "%s---%s từ %s, tên là %s, trao quyền cho các doanh nhân đa dạng, thu hẹp khoảng cách tài chính cho các cộng đồng chưa được đại diện. Cam kết của họ đối với tài chính toàn diện thúc đẩy bình đẳng kinh tế.",
        [15] = "%s---%s ở %s, tên là %s, giúp các gia đình đảm bảo tương lai của họ thông qua việc lập kế hoạch di sản thận trọng. Hướng dẫn đầy lòng trắc ẩn của cô ấy làm giảm sự phức tạp của việc chuyển giao tài sản.",
        [16] = "%s---%s từ %s, một nhà ngân hàng giàu kinh nghiệm tên là %s, xuất sắc trong tài chính doanh nghiệp, cung cấp các giải pháp chiến lược cho các công ty đa quốc gia. Sự nhạy bén về tài chính của họ thúc đẩy thành công trên các thị trường toàn cầu.",
        [17] = "%s---%s ở %s, tên là %s, là một người quản lý tài sản đáng tin cậy, bảo toàn và phát triển tài sản của khách hàng cho các thế hệ mai sau. Cách tiếp cận toàn diện của cô ấy đảm bảo sự ổn định tài chính lâu dài.",
        [18] = "%s---%s từ %s, tên là %s, là một nhà ngân hàng am hiểu công nghệ, tận dụng các đổi mới kỹ thuật số để nâng cao trải nghiệm khách hàng. Cam kết của họ đối với ngân hàng liền mạch nâng cao sự hài lòng của khách hàng.",
        [19] = "%s---%s ở %s, tên là %s, thúc đẩy kiến ​​thức tài chính trong giới trẻ, truyền cảm hứng cho thế hệ nhà đầu tư thông minh tiếp theo. Các sáng kiến ​​giáo dục của cô ấy xây dựng một nền tảng vững chắc cho sự thành công về tài chính.",
        [20] = "%s---%s từ %s, một người có tầm nhìn tên là %s, thúc đẩy sự thay đổi mang tính chuyển đổi trong ngành ngân hàng thông qua các phương pháp bền vững. Khả năng lãnh đạo của họ định hình một bối cảnh tài chính có trách nhiệm và tác động hơn.",
    },

    ["nơi cư trú"] = { -- Hidden residence prompts (First %s: First name / Second %s: Age / Third %s: First letter of Residence, Fourth %s: Last letter of Residence)
        [1] = "Gặp gỡ %s, thần đồng ngân hàng. Chỉ mới %s tuổi, họ là một trong những nhà phân tích dữ liệu trẻ nhất và thông minh nhất của bang %s---%s. Một nhà tư tưởng có tầm nhìn với tính chính trực không thay đổi, Alex tìm cách cách mạng hóa lĩnh vực ngân hàng và tạo ra tác động lâu dài đến tương lai tài chính của khách hàng.",
        [2] = "Giới thiệu %s, một thiên tài ngân hàng, chỉ mới %s tuổi và đã là một trong những nhà phân tích dữ liệu sáng giá nhất trong bang sôi động %s---%s. Với tư duy có tầm nhìn và đạo đức không thay đổi, họ hướng tới việc định hình lại ngành ngân hàng và tạo ra tác động tích cực lâu dài đến hành trình tài chính của khách hàng.",
