    ["tuổi"] = { -- Hidden age prompt (First %s: First name / Second %s: Residence / *: Age)
        [1] = "%s ở %s, một nhà phân tích tài chính có đạo đức, thúc đẩy các khoản đầu tư bền vững vì một thế giới xanh hơn. Với tinh thần trách nhiệm sâu sắc, nhà phân tích giàu kinh nghiệm này, * tuổi, phù hợp với các mục tiêu tài chính của khách hàng với việc bảo tồn môi trường.",
        [2] = "%s từ %s, phát triển mạnh trong việc giải quyết các thách thức về thuế phức tạp cho các khách hàng doanh nghiệp. Với kỹ năng phân tích sắc bén và kinh nghiệm phong phú, chuyên gia thuế này, * tuổi, dễ dàng điều hướng các tình huống tài chính phức tạp.",
        [3] = "%s ở %s, xây dựng mối quan hệ đối tác lâu dài với các tổ chức từ thiện, thúc đẩy tác động xã hội tích cực. Với niềm đam mê làm từ thiện và kinh nghiệm phong phú, chiến lược gia từ thiện này, * tuổi, tận dụng kiến ​​thức chuyên môn về tài chính của họ để nâng cao hiệu quả của các sáng kiến ​​xã hội.",
        [4] = "%s từ %s, là người tiên phong trong lĩnh vực đầu tư, định hình lại ngành với các công nghệ sáng tạo. Ở tuổi *, nhà đầu tư có tầm nhìn xa này hình dung ra một kỷ nguyên mới của các dịch vụ đầu tư lấy khách hàng làm trung tâm được tăng cường bởi những tiến bộ công nghệ.",
        [5] = "%s ở %s, chuyên về sáp nhập và mua lại, tổ chức các giao dịch thành công cho các khách hàng doanh nghiệp. Với con mắt tinh tường về cơ hội và nhiều năm kinh nghiệm, chuyên gia M&A này, * tuổi, tối đa hóa giá trị cho tất cả các bên liên quan trong các giao dịch phức tạp.",
        [6] = "%s từ %s, trao quyền cho các doanh nhân đa dạng, thu hẹp khoảng cách tài chính cho các cộng đồng chưa được đại diện. Với cam kết tài chính toàn diện và kinh nghiệm phong phú, cố vấn phát triển kinh doanh này, * tuổi, thúc đẩy bình đẳng kinh tế cho tất cả các doanh nhân đầy tham vọng.",
